// Ejemplo de uso con la biblioteca de la API de OpenAI
const response = await openai.chat.completions.create({
  model: 'gpt-4',
  messages: [{ role: 'user', content: 'Hola, ¿cómo estás?' }],
});
console.log(response.choices[0].message.content);

// Usar COCO con el API de torchvision
from torchvision.datasets import CocoDetection
coco = CocoDetection(root='imagenes/', annFile='annotations/instances_train.json')

/* Ejemplo de prompt para traducir del inglés al español */
Eres un traductor profesional. Traduce el siguiente texto al español manteniendo el tono y el contexto original.
Texto: 'The quick brown fox jumps over the lazy dog.'

// Uso del clasificador de sentimientos con Hugging Face Transformers
import { pipeline } from '@huggingface/transformers';
const sentiment = await pipeline('sentiment-analysis', 'pierreguillou/bert-base-cased-sentiment');
const result = await sentiment('Me encanta este producto');
console.log(result);
